PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE IF NOT EXISTS "alternate_ingredients" (
	"id"	INTEGER,
	"recipe_id"	INTEGER NOT NULL,
	"original_ingredient"	INTEGER,
	"alternate_ingredient"	INTEGER NOT NULL,
	"quantity"	NUMERIC NOT NULL,
	"unit_id"	INTEGER NOT NULL,
	"extensions"	TEXT NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "cookbook_recipes" (
	"id"	integer NOT NULL,
	"cookbook_id"	integer NOT NULL,
	"recipe_id"	integer NOT NULL,
	"ordering"	integer,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "cookbooks" (
	"id"	integer NOT NULL,
	"name"	text NOT NULL,
	"description"	text,
	"created"	integer,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "extensions" (
	"id"	integer NOT NULL,
	"name"	text NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "ingredients" (
	"id"	integer NOT NULL,
	"name"	text NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "recipe_extensions" (
	"id"	integer NOT NULL,
	"recipe_id"	integer NOT NULL,
	"extension_id"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "recipe_ingredients" (
	"id"	integer NOT NULL,
	"recipe_id"	integer NOT NULL,
	"ingredient_id"	integer NOT NULL,
	"quantity"	NUMERIC NOT NULL,
	"unit"	integer NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "recipes" (
	"id"	integer NOT NULL,
	"title"	text NOT NULL,
	"minTemp"	text,
	"itemType"	text,
	"portionSize"	text,
	"calories"	text,
	"category"	text,
	"instructions"	text,
	"ccp"	text,
	"substitutions"	text,
	"initialServings"	integer,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "units" (
	"id"	integer NOT NULL,
	"name"	text NOT NULL,
	"conversion_unit"	text,
	"conversion_threshold"	real,
	"conversion_formula"	real,
	PRIMARY KEY("id" AUTOINCREMENT)
);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "extensions_name_unique" ON "extensions" (
	"name"
);
CREATE UNIQUE INDEX "ingredients_name_unique" ON "ingredients" (
	"name"
);
CREATE UNIQUE INDEX "units_name_unique" ON "units" (
	"name"
);
